&quot;isDescendentOfQueryLoop&quot;:false,&quot;textAlign&quot;:&quot;&quot;,&quot;children&quot;:[]}]],&quot;orderby&quot;:&quot;menu_order&quot;,&quot;rows&quot;:3}\"><!-- wp:woocommerce/product-image {\"imageSizing\":\"cropped\"} -->\n<div class=\"is-loading\"></div>\n<!-- /wp:woocommerce/product-image -->\n\n<!-- wp:woocommerce/product-title -->\n<div class=\"wp-block-woocommerce-product-title is-loading\"></div>\n<!-- /wp:woocommerce/product-title -->\n\n<!-- wp:woocommerce/product-price -->\n<div class=\"is-loading\"></div>\n<!-- /wp:woocommerce/product-price -->\n\n<!-- wp:woocommerce/product-rating -->\n<div class=\"is-loading\"></div>\n<!-- /wp:woocommerce/product-rating -->\n\n<!-- wp:woocommerce/product-button -->\n<div class=\"is-loading\"></div>\n<!-- /wp:woocommerce/product-button --></div>\n<!-- /wp:woocommerce/all-products -->', 'Shop', '', 'publish', 'closed', 'closed', '', 'shop', '', '', '2023-04-12 22:22:13', '2023-04-12 19:22:13', '', 0, 'http://kp4.test/shop/', 0, 'page', '', 0),
