(176, 1, '2023-04-13 18:50:29', '2023-04-13 15:50:29', '<!-- wp:shortcode -->\n[woocommerce_my_account]\n<!-- /wp:shortcode -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:woocommerce/customer-account /-->', 'My account', '', 'inherit', 'closed', 'closed', '', '9-revision-v1', '', '', '2023-04-13 18:50:29', '2023-04-13 15:50:29', '', 9, 'http://kp4.test/?p=176', 0, 'revision', '', 0),
